import { useState } from "react";
import { getArticles } from "../../services/fakeArticlesService";
import Article from "./article/article";
import Pagination from "../common/pagination/pagination";
import { paginate } from "../utils/paginate";
import TagsList from "../common/tagsList/tagsList";
import styled from "styled-components";
import { Link } from "react-router-dom";

const ArticleList = () => {
  const [articles, setArticles] = useState(getArticles());
  const [pageSize] = useState(4);
  const [currentPage, setCurrentPage] = useState(1);

  const handleLike = (article) => {
    //call server to process the changes
    const myArticles = [...articles];
    const index = myArticles.indexOf(article);
    myArticles[index] = { ...myArticles[index] };
    myArticles[index].favorited = !myArticles[index].favorited;
    setArticles(myArticles);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const paginatedArticles = paginate(articles, currentPage, pageSize);

  return (
    <ListsWrapper>
      <div className="articles-preview">
        <FeedUl>
          <FeedLi>
            <FeedLink className="feed-header" to="#">
              Global Feed
            </FeedLink>
          </FeedLi>
          <FeedLi>
            <FeedLink className="feed-header" to="#">
              Global Feed 2
            </FeedLink>
          </FeedLi>
        </FeedUl>
        {paginatedArticles.map((article) => (
          <Article
            key={article.slug}
            article={article}
            onClick={() => handleLike(article)}
          ></Article>
        ))}
        <Pagination
          itemsCount={articles.length}
          pageSize={pageSize}
          onPageChange={handlePageChange}
          currentPage={currentPage}
        ></Pagination>
      </div>
      <div className="tags-list">
        <TagsList />
      </div>
    </ListsWrapper>
  );
};

export default ArticleList;

const ListsWrapper = styled.div`
  /* background-color: red; */
`;

const FeedUl = styled.ul`
  margin: 0px 20%;
  display: flex;
  list-style: none;
`;

const FeedLi = styled.li``;

const FeedLink = styled(Link)`
  padding: 0.5em 1em;

  text-decoration: none;
  color: #aaa;
  :hover {
    color: ;
  }
`;
