import { useState } from "react";
import { getArticles } from "../../services/fakeArticlesService";
import Article from "./article/article";
import Pagination from "../common/pagination/pagination";
import { paginate } from "../utils/paginate";
import TagsList from "../common/tagsList/tagsList";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { getTags } from "../../services/fakeTagsService";

const ArticleList = () => {
  const [articles, setArticles] = useState(getArticles());
  const [pageSize] = useState(4);
  const [currentPage, setCurrentPage] = useState(1);
  const [tags, setTags] = useState(getTags());

  const handleLike = (article) => {
    //call server to process the changes
    const myArticles = [...articles];
    const index = myArticles.indexOf(article);
    myArticles[index] = { ...myArticles[index] };
    myArticles[index].favorited = !myArticles[index].favorited;
    setArticles(myArticles);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const paginatedArticles = paginate(articles, currentPage, pageSize);

  const handleTagSelect = (tag) => {
    console.log(tag);
  };
  return (
    <RowContainer>
      <ArticlesPreview>
        <FeedUl>
          <li>
            <FeedLink className="feed-header" to="#">
              Global Feed
            </FeedLink>
          </li>
          <li>
            <FeedLink className="feed-header" to="#">
              #SomeHashTag
            </FeedLink>
          </li>
        </FeedUl>
        {paginatedArticles.map((article) => (
          <Article
            key={article.slug}
            article={article}
            onClick={() => handleLike(article)}
          ></Article>
        ))}
        <Pagination
          itemsCount={articles.length}
          pageSize={pageSize}
          onPageChange={handlePageChange}
          currentPage={currentPage}
        ></Pagination>
      </ArticlesPreview>
      <div className="tags-list">
        <TagsList items={tags} onItemSelect={handleTagSelect} />
      </div>
    </RowContainer>
  );
};

export default ArticleList;

const RowContainer = styled.div`
  display: flex;
  justify-content: center;
`;
const ArticlesPreview = styled.div`
  /* flex: 1; */
`;

//article list styles
const FeedUl = styled.ul`
  margin: 0px 20%;
  display: flex;
  justify-content: flex-start;
  list-style: none;

  padding-bottom: 7px;
`;

const FeedLink = styled(Link)`
  padding: 0.5em 1em;
  text-decoration: none;
  color: #aaa;
  :hover {
    color: rgba(0, 0, 0, 0.671);
    border-bottom: 2px solid #5cb85c;
  }
  transition: all 0.3s ease 0s;
`;
