import React, { Component } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Heart from "./heart.png";

const Article = (props) => {
  return (
    <ArticleContainer>
      <ArticleInfo>
        <ArticleMeta>
          <ImgLink to="#">
            <AuthorImg src={this.state.author.image}></AuthorImg>
          </ImgLink>
          <AuthorMetaContainer>
            <NameLink to="#">{this.state.author.username}</NameLink>
            <MetaDate>Thu Mar 04 2021</MetaDate>
          </AuthorMetaContainer>
        </ArticleMeta>
        <ArticleContent>
          <ArticleTitle>{this.state.title}</ArticleTitle>
          <ArticleBody>{this.state.body}</ArticleBody>
        </ArticleContent>
        <ReadMoreDiv>Read more...</ReadMoreDiv>
      </ArticleInfo>
      <LikeButton>
        <HeartIcon src={Heart}></HeartIcon>
        <p>{this.state.favoritesCount}</p>
      </LikeButton>
    </ArticleContainer>
  );
};

export default Article;

//article style

const ArticleContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 0% 20%;
`;

const ArticleInfo = styled.div`
  /* border-top: 1px solid rgba(17, 190, 196, 0.1); */
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
`;

const ArticleMeta = styled.div`
  display: flex;
  justify-content: flex-start;
  margin-bottom: 16px;
`;

const AuthorMetaContainer = styled.div`
  text-align: left;
  padding-left: 5px;
  display: flex;
  flex-direction: column;
`;

const ImgLink = styled(Link)`
  height: 32px;
  width: 32px;
`;
const AuthorImg = styled.img`
  height: 100%;
  width: 100%;
  border-radius: 30px;
`;

const NameLink = styled(Link)`
  color: #5cb85c;
  font-weight: 700;
  text-decoration: none;
  :hover {
    text-decoration: underline;
  }
`;

const MetaDate = styled.span`
  color: #bbb;
  font-size: 0.8rem;
`;

const ArticleContent = styled.div`
  cursor: pointer;
  margin-bottom: 16px;
  text-align: left;
`;

const ArticleTitle = styled.h3`
  text-decoration: none;
  font-weight: 700;
  font-size: 1.5rem;
`;

const ArticleBody = styled(ArticleContent)`
  color: #999;
`;
const ReadMoreDiv = styled(ArticleContent)`
  text-align: left;
  color: #bbb;
  font-size: 0.8rem;
`;
const HeartIcon = styled.img`
  width: 12.375px;
  height: 13px;
  margin-right: 2px;
`;

const LikeButton = styled.button`
  width: 40px;
  height: 25px;
  text-align: center;
  text-decoration: none;
  background-color: transparent;
  border: 1px solid #5cb85c;
  color: #5cb85c;
  display: inline-flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.25rem 0.5rem;
  border-radius: 0.2rem;
  font-size: 0.875rem;
  cursor: pointer;
  :hover {
    background-color: #5cb85c;
    color: #fff;
    ${HeartIcon} {
      filter: brightness(0) invert(1);
    }
  }
`;
