import Article from "../article/article";
import * as styled from "./articlesPreviewStyles";

const ArticlesPreview = ({
  articles,
  onLike,
  selectedTag,
  onItemSelect,
  items,
}) => {
  const GlobalTag = () => {
    const globalItem = items[0];
    return (
      <styled.FeedTab>
        {selectedTag === undefined ? (
          <li>
            <styled.GlobalFeedLink selectedtag={selectedTag} to="/">
              Global Feed
            </styled.GlobalFeedLink>
          </li>
        ) : (
          <li>
            <styled.FeedLink onClick={() => onItemSelect(globalItem)} to="/">
              Global Feed
            </styled.FeedLink>
            <styled.FeedLink selectedTag={selectedTag} to="#">
              {`#` + selectedTag}
            </styled.FeedLink>
          </li>
        )}
      </styled.FeedTab>
    );
  };

  return (
    <div>
      <GlobalTag />
      {articles.map((article) => (
        <Article
          key={article.slug}
          article={article}
          onClick={onLike(article)}
        ></Article>
      ))}
    </div>
  );
};

export default ArticlesPreview;
