import styled from "styled-components";
import { Link } from "react-router-dom";

export const RowContainer = styled.div`
  padding: 0px 20%;
  display: flex;
  /* justify-content: center; */
`;
export const ArticlesColumn = styled.div`
  flex: 3;
  padding-right: 15px;
  padding-left: 15px;
`;

export const TagsColumn = styled.div`
  flex: 1;
  padding-right: 15px;
  padding-left: 15px;
`;

//article list styles
export const FeedUl = styled.ul`
  display: flex;
  justify-content: flex-start;
  list-style: none;
  padding-bottom: 7px;
`;

export const GlobalFeedLink = styled(Link)`
  padding: 0.5em 1em;
  text-decoration: none;
  color: #aaa;
  cursor: default;
`;

export const FeedLink = styled(Link)`
  padding: 0.5em 1em;
  text-decoration: none;
  color: #aaa;
  :hover {
    color: rgba(0, 0, 0, 0.671);
    border-bottom: 2px solid #5cb85c;
  }
  transition: all 0.3s ease 0s;
`;
