import * as styled from "./tagsStyles";
const Tags = ({ items, onItemSelect, selectedItem, loadingTags }) => {
  return (
    <styled.TagSideBar>
      <p>Popular Tags</p>
      {loading && <p>loading tags..</p>}
      {!loading && (
        <>
          <styled.TagContainer>
            {items.map((item) =>
              !(item === "Global Feed") ? (
                <styled.TagLink
                  onClick={() => onItemSelect(item)}
                  key={item}
                  item={item}
                  curritem={selectedItem}
                  to="#"
                >
                  {item}
                </styled.TagLink>
              ) : null
            )}
          </styled.TagContainer>
        </>
      )}
    </styled.TagSideBar>
  );
};

export default Tags;
