import styled from "styled-components";
import { Link } from "react-router-dom";

export const ArticleWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 0% 20%;
  padding-top: 15px;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
`;

export const ArticleInfo = styled.div`
  display: flex;
  flex-direction: column;
`;

export const ArticleMeta = styled.div`
  display: flex;
  margin-bottom: 10px;
`;

export const AuthorMetaContainer = styled.div`
  text-align: left;
  padding-left: 5px;
  display: flex;
  flex-direction: column;
`;
export const MetaName = styled(Link)`
  color: #5cb85c;
  font-weight: 700;
  text-decoration: none;
  :hover {
    text-decoration: underline;
    color: #3f8d3f;
  }
`;

export const MetaDate = styled.span`
  color: #bbb;
  font-size: 0.8rem;
`;

export const ImgContainer = styled(Link)`
  height: 35px;
  width: 35px;
`;
export const AuthorImg = styled.img`
  height: 100%;
  width: 100%;
  border-radius: 30px;
`;

export const ArticleContent = styled.div`
  cursor: pointer;
  margin-bottom: 16px;
  text-align: left;
`;

export const ArticleTitle = styled.h3`
  text-decoration: none;
  font-weight: 700;
  font-size: 1.5rem;
`;

export const ArticleBody = styled(ArticleContent)`
  color: #999;
`;
export const ReadMoreDiv = styled(ArticleContent)`
  color: #bbb;
  font-size: 0.8rem;
`;
export const HeartIcon = styled.img`
  width: 13px;
  height: 12px;
  padding-right: 2px;
  filter: ${(props) =>
    props.favorited ? "brightness(0) invert(1)" : "brightness (1) invert(0)"};
`;

export const LikeButton = styled.button`
  width: 40px;
  height: 25px;
  text-align: center;
  text-decoration: none;
  background-color: ${(props) => (!props.favorited ? "white" : "#5cb85c")};
  color: ${(props) => (props.favorited ? "#fff" : "#5cb85c")};
  border: 1px solid #5cb85c;
  display: inline-flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.25rem 0.5rem;
  border-radius: 0.2rem;
  font-size: 0.875rem;
  cursor: pointer;
  :hover {
    background-color: #5cb85c;
    color: #fff;
    ${HeartIcon} {
      filter: brightness(0) invert(1);
    }
  }
`;
