import { useState } from "react";
import { getArticles } from "../../../services/fakeArticlesService";
import { getTags } from "../../../services/fakeTagsService";
import Article from "../article/article";
import Pagination from "../../common/pagination/pagination";
import { paginate } from "../../utils/paginate";
import TagsList from "../../common/tagsList/tagsList";
import * as styled from "./articleListStyles";

const ArticleList = () => {
  const [articles, setArticles] = useState(getArticles());
  const [pageSize] = useState(4);
  const [currentPage, setCurrentPage] = useState(1);
  const [tags] = useState(getTags());
  const [selectedTag, setCurrentTag] = useState();
  const handleLike = (article) => {
    const myArticles = [...articles];
    const index = myArticles.indexOf(article);
    myArticles[index] = { ...myArticles[index] };
    myArticles[index].favorited = !myArticles[index].favorited;
    setArticles(myArticles);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const paginatedArticles = paginate(articles, currentPage, pageSize);

  const handleTagSelect = (tag) => {
    setCurrentTag(tag);
  };

  return (
    <styled.RowContainer>
      <styled.ArticlesColumn>
        <styled.FeedUl>
          {selectedTag === undefined ? (
            <li>
              <styled.GlobalFeedLink to="#">Global Feed</styled.GlobalFeedLink>
            </li>
          ) : (
            <div>
              <li>
                <styled.FeedLink to="#">Global Feed</styled.FeedLink>
              </li>
              <p>{selectedTag}</p>
            </div>
          )}
        </styled.FeedUl>
        {paginatedArticles.map((article) => (
          <Article
            key={article.slug}
            article={article}
            onClick={() => handleLike(article)}
          ></Article>
        ))}
        <Pagination
          itemsCount={articles.length}
          pageSize={pageSize}
          onPageChange={handlePageChange}
          currentPage={currentPage}
        ></Pagination>
      </styled.ArticlesColumn>
      <styled.TagsColumn>
        <TagsList
          items={tags}
          onItemSelect={handleTagSelect}
          selectedItem={selectedTag}
        />
      </styled.TagsColumn>
    </styled.RowContainer>
  );
};

export default ArticleList;
