import { useState, useEffect } from "react";
import axios from "axios";
import { getArticles } from "../../../services/fakeArticlesService";
import ArticlesPreview from "../articlesPreview/articlesPreview";
import { getTags } from "../../../services/fakeTagsService";
import TagsNav from "../../tagsNav/tagsNav";
import Pagination from "../../common/pagination/pagination";
import { paginate } from "../../utils/paginate";
import Tags from "../../common/tags/tags";
import * as styled from "./mainViewStyles";

const MainView = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [pageSize] = useState(10);
  const [currentPage, setCurrentPage] = useState(1);
  const [tags] = useState(["Global Feed", ...getTags()]);
  const [selectedTag, setSelectedTag] = useState();
  const apiEndPoint = "https://conduit.productionready.io/api/articles";

  useEffect(() => {
    axios({
      method: "GET",
      url: "apiEndPoint ",
    }).then((response) => {
      setArticles(response.data.articles);
    });
    setLoading(false);
  });

  const handleLike = (article) => {
    const myArticles = [...articles];
    const index = myArticles.indexOf(article);
    myArticles[index] = { ...myArticles[index] };
    myArticles[index].favorited = !myArticles[index].favorited;
    setArticles(myArticles);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleTagSelect = (tag) => {
    tag === "Global Feed" ? setSelectedTag(undefined) : setSelectedTag(tag);
    setCurrentPage(1);
  };

  const filteredArticles = selectedTag
    ? articles.filter((article) => article.tagList.includes(selectedTag))
    : articles;

  const paginatedArticles = paginate(filteredArticles, currentPage, pageSize);

  return (
    <styled.RowContainer>
      <styled.ArticlesColumn>
        {loading && (
          <>
            <TagsNav
              selectedTag={selectedTag}
              onItemSelect={handleTagSelect}
              items={tags}
            />
            <styled.Loading className="loading">Loading...</styled.Loading>
          </>
        )}
        {!loading && (
          <>
            <TagsNav
              selectedTag={selectedTag}
              onItemSelect={handleTagSelect}
              items={tags}
            />
            <ArticlesPreview
              articles={paginatedArticles}
              onLike={() => handleLike}
            />
            <Pagination
              itemsCount={filteredArticles.length}
              pageSize={pageSize}
              onPageChange={handlePageChange}
              currentPage={currentPage}
            />
          </>
        )}
      </styled.ArticlesColumn>
      <styled.TagsColumn>
        <Tags
          items={tags}
          onItemSelect={handleTagSelect}
          selectedItem={selectedTag}
        />
      </styled.TagsColumn>
    </styled.RowContainer>
  );
};

export default MainView;
