import { useState } from "react";
import { getArticles } from "../../services/fakeArticlesService";
import Article from "./article/article";
import Pagination from "../common/pagination";

const ArticleList = () => {
  const [articles, setArticles] = useState(getArticles());

  const handleLike = (article) => {
    //call server to process the changes
    const myArticles = [...articles];
    const index = myArticles.indexOf(article);
    myArticles[index] = { ...myArticles[index] };
    myArticles[index].favorited = !myArticles[index].favorited;
    setArticles(myArticles);
  };

  return (
    <div>
      {articles.map((article) => (
        <Article
          key={article.slug}
          article={article}
          onClick={() => handleLike(article)}
        ></Article>
      ))}
      <Pagination itemsCount={articles.length} pageSize={4}></Pagination>
    </div>
  );
};

export default ArticleList;
