import React, { Component } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

class Article extends Component {
  state = {
    slug: "how-to-train-your-dragon",
    title: "How to train your dragon",
    description: "Ever wonder how?",
    body: "It takes a Jacobian",
    tagList: ["dragons", "training"],
    createdAt: "2016-02-18T03:22:56.637Z",
    updatedAt: "2016-02-18T03:48:35.824Z",
    favorited: false,
    favoritesCount: 0,
    author: {
      username: "jake",
      bio: "I work at statefarm",
      image: "https://static.productionready.io/images/smiley-cyrus.jpg",
      following: false,
    },
  };

  render() {
    return (
      <ArticleContainer>
        <ArticleInfo>
          <ArticleMeta>
            <ImgLink to="#">
              <AuthorImg src={this.state.author.image}></AuthorImg>
            </ImgLink>

            <AuthorMetaContainer>
              <NameLink to="#">{this.state.author.username}</NameLink>
              <MetaDate>Thu Mar 04 2021</MetaDate>
            </AuthorMetaContainer>
          </ArticleMeta>
          <ArticleContent>
            <p className="article-title">Title here</p>
            <p className="article-body">Body here</p>
          </ArticleContent>
          <ReadMoreDiv>read more</ReadMoreDiv>
        </ArticleInfo>
        <div className="like-button">
          <button className="favorite-button">Like</button>
        </div>
      </ArticleContainer>
    );
  }
}

export default Article;

//article style

const ArticleContainer = styled.div`
  display: flex;
  justify-content: space-between;
  background-color: #5b5bbb;
  margin: 0% 20%;
`;

const ArticleInfo = styled.div`
  /* border-top: 1px solid rgba(17, 190, 196, 0.1); */
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
`;

const ArticleMeta = styled.div`
  display: flex;
  justify-content: flex-start;
  background-color: red;
  margin-bottom: 16px;
`;

const AuthorMetaContainer = styled.div`
  text-align: left;
  padding-left: 5px;
  display: flex;
  flex-direction: column;
`;

const ImgLink = styled(Link)`
  height: 32px;
  width: 32px;
`;
const AuthorImg = styled.img`
  height: 100%;
  width: 100%;
  border-radius: 30px;
`;

const NameLink = styled(Link)`
  color: #5cb85c;
  font-weight: 700;
  text-decoration: none;
`;

const MetaDate = styled.span`
  color: #bbb;
  font-size: 0.8rem;
`;

const ArticleContent = styled.div`
  margin-bottom: 16px;
`;
